INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2017-10-04 15:35:52', '2017-10-04 15:35:52', 'Welcome to WordPress. This is your first post. Edit or delete it, then start writing!', 'Hello world!', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2017-10-05 17:28:23', '2017-10-05 17:28:23', '', 0, 'http://localhost:8080/website1/?p=1', 0, 'post', '', 1),
(2, 1, '2017-10-04 15:35:52', '2017-10-04 15:35:52', 'This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my website. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin\' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Gotham City, XYZ employs over 2,000 people and does all kinds of awesome things for the Gotham community.</blockquote>\n\nAs a new WordPress user, you should go to <a href=\"http://localhost:8080/website1/wp-admin/\">your dashboard</a> to delete this page and create new pages for your content. Have fun!', 'Sample Page', '', 'trash', 'closed', 'open', '', 'sample-page__trashed', '', '', '2017-10-07 10:27:25', '2017-10-07 10:27:25', '', 0, 'http://localhost:8080/website1/?page_id=2', 0, 'page', '', 0),
(6, 1, '2017-10-05 15:39:26', '2017-10-05 15:39:26', 'Welcome to our online store! Feel free to browse around:\n\n[mp_store_navigation]\n\nCheck out our most popular products:\n\n[mp_popular_products]\n\nBrowse by category:\n\n[mp_list_categories]\n\nBrowse by tag:\n\n[mp_tag_cloud]', 'Store', '', 'trash', 'closed', 'closed', '', 'store__trashed', '', '', '2017-10-20 16:44:52', '2017-10-20 16:44:52', '', 0, 'http://localhost:8080/website1/store/', 0, 'page', '', 0),
(7, 1, '2017-10-05 15:39:27', '2017-10-05 15:39:27', '[mp_list_products]', 'Products', '', 'publish', 'closed', 'closed', '', 'products', '', '', '2017-10-20 17:22:14', '2017-10-20 17:22:14', '', 6, 'http://localhost:8080/website1/store/products/', 0, 'page', '', 0),
(8, 1, '2017-10-05 15:39:27', '2017-10-05 15:39:27', '[mp_cart]', 'Cart', '', 'publish', 'closed', 'closed', '', 'cart', '', '', '2017-10-05 15:39:27', '2017-10-05 15:39:27', '', 6, 'http://localhost:8080/website1/store/cart/', 0, 'page', '', 0),
(9, 1, '2017-10-05 15:39:27', '2017-10-05 15:39:27', '[mp_checkout]', 'Checkout', '', 'publish', 'closed', 'closed', '', 'checkout', '', '', '2017-10-05 15:39:27', '2017-10-05 15:39:27', '', 6, 'http://localhost:8080/website1/store/checkout/', 0, 'page', '', 0),
